package com.kodnest.pratice;

public class triangle {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int rows=4;
for(int i=1;i<=4;i++) {
	for(int j=1;j<=4;j++) {
		System.out.print("*");
	}
	System.out.println();
	
}
for(int i=rows-1;i<=4;i++) {
	for(int j=1;j<=4;j++) {
	System.out.print("%");
	}
System.out.println();
}
	}
}
